// Obt√©m o FlowFile da sess√£o
def flowFile = session.get()
if (!flowFile) return // Se n√£o houver FlowFile, sai do script

// Recupera o valor atual do atributo 'iteracao' (se existir), ou define como 0
def iteracao = flowFile.getAttribute('iteracao')?.toInteger() ?: 0

// Incrementa o valor de 'iteracao'
iteracao++

// Atribui o valor incrementado de volta ao FlowFile
flowFile = session.putAttribute(flowFile, 'iteracao', iteracao.toString())

// Transferir para o relacionamento de sucesso
session.transfer(flowFile, REL_SUCCESS)

// Obter todos os FlowFiles da fila
def flowFiles = []
def flowFile = session.get()

// Contadores para FlowFiles com _IS_PRODUCT
def countIsProductFalseBroilers = 0
def countIsProductTrueBroilers = 0

// Enquanto houver FlowFiles, adiciona √† lista e conta os FlowFiles com o atributo '_IS_PRODUCT'
while (flowFile != null) {
    flowFiles.add(flowFile)

    // Verifica o valor do atributo '_IS_PRODUCT' e conta de acordo
    def isProduct = flowFile.getAttribute('_IS_PRODUCT')
    if (isProduct == 'false') {
        countIsProductFalseBroilers++
    } else if (isProduct == 'true') {
        countIsProductTrueBroilers++
    }

    // Obter o pr√≥ximo FlowFile
    flowFile = session.get()
}

// Atribuir os valores de totalComp e totalMeds somente aos FlowFiles com '_IS_PRODUCT' == 'true'
flowFiles.each { currentFlowFile ->
    def isProduct = currentFlowFile.getAttribute('_IS_PRODUCT')

    // Se o FlowFile tem _IS_PRODUCT = 'true', atribui os valores de totalComp e totalMeds
    if (isProduct == 'true') {
        // Atribui o n√∫mero de FlowFiles com _IS_PRODUCT = 'false' (totalComp)
        currentFlowFile = session.putAttribute(currentFlowFile, 'totalComp', countIsProductFalseBroilers.toString())
        // Atribui o n√∫mero de FlowFiles com _IS_PRODUCT = 'true' (totalMeds)
        currentFlowFile = session.putAttribute(currentFlowFile, 'totalMeds', countIsProductTrueBroilers.toString())
    }

    // Transferir para o relacionamento de sucesso
    session.transfer(currentFlowFile, REL_SUCCESS)
}

